import React, {Component} from "react";
import "./main.css";



class Towers extends Component {
    constructor(props) {
        super(props);
        this.state = props.state;
    }



towers = (number) => {
    if (number == 1) {
        if (this.state.potatoes >= this.state.spudSpitterCost) {
            this.state.spudSpitter += 1;
            this.state.potatoes -= this.state.spudSpitterCost;
            this.state.spudSpitterCost = Math.floor(10 * (1.25 ** this.state.spudSpitter));
            this.state.potatoUp += 1;

            if (this.state.potatoes < this.state.spudSpitterCost) {
                document.getElementById("spudSpitter").style.backgroundColor = '#006600';
            }

            document.getElementById("varCount").innerHTML = "Potatoes: " + this.state.potatoes.toLocaleString();
            document.getElementById("spudSpitterCount").innerHTML = "Spud Spitters: " + this.state.spudSpitter.toLocaleString();
            document.getElementById("spudSpitterText").innerHTML = "Cost: " + this.state.spudSpitterCost.toLocaleString();
            console.log(this.state.spudSpitter);
            }
    } else if (number == 2) {
        if (this.state.potatoes >= this.state.farmerCost) {
            this.state.farmer += 1;
            this.state.potatoes -= this.state.farmerCost;
            this.state.farmerCost = Math.floor(50 * (1.15 ** this.state.farmer));
            this.state.PpS += this.state.farmerPpSadd;

            if (this.state.potatoes < this.state.farmerCost) {
                document.getElementById("potatoFarmer").style.backgroundColor = '#006600';
            }

            document.getElementById("varCount").innerHTML = "Potatoes: " + this.state.potatoes.toLocaleString();
            document.getElementById("PpSCount").innerHTML = "PpS: " + this.state.PpS.toLocaleString();
            document.getElementById("farmerCount").innerHTML = "Potato Farmers: " + this.state.farmer.toLocaleString();
            document.getElementById("farmerText").innerHTML = "Cost: " + this.state.farmerCost.toLocaleString();
            console.log(this.state.farmer);
        }
    } else if (number == 3) {
        if (this.state.potatoes >= this.state.farmCost) {
            
        }
    } else if (number == 4) {
        if (this.state.potatoes >= this.state.factoryCost) {
            
        }
    } else if (number == 5) {
        if (this.state.potatoes >= this.state.districtCost) {
            
        }
    } else if (number == 6) {
        if (this.state.potatoes >= this.state.conventionCost) {
            
        }
    } else if (number == 7) {
        if (this.state.potatoes >= this.state.merchandiseCost) {
            
        }
    }  else if (number == 8) {
        if (this.state.potatoes >= this.state.cultCost) {
            
        }
    }  else if (number == 9) {
        if (this.state.potatoes >= this.state.sponsorshipsCost) {
            
        }
    }
}



    render() {
        return(
            <div>
                

                <br />
                <br />

                {/* Spud Spitter Tower Button */}
                <button class="towers" id="spudSpitter" type="button" onClick={this.towers(1)}>
                    <p>Spud Spitter</p>
                    <p class="towerText" id="spudSpitterText">Cost: {this.state.spudSpitterCost}</p>
                    <p class="towerDesc">Makes more potatoes per click.</p>
                </button>

                <br />

                {/* Potato Farmer Tower Button */}
                <button class="towers" id="potatoFarmer" type="Button" onClick={this.towers(2)}>
                    <p>Potato Farmer</p>
                    <p class="towerText" id="farmerText">Cost: {this.state.farmerCost}</p>
                    <p class="towerDesc" id="farmerDesc">This Honest Man gives you his potatoes &#40;{this.state.farmerPpSadd} PpS&#41;</p>
                </button>

                {/* Potato Farm Tower Button */}
                <button class="towers" id="potatoFarm" type="Button" onClick={this.towers(3)}>
                    <p>Potato Farm</p>
                    <p class="towerText" id="farmText">Cost: 300</p>
                    <p class="towerDesc" id="farmDesc">A whole farm dedicated to your potatoes. &#40;{this.state.farmPpSadd} PpS&#41;</p>
                </button>

                <br />

                {/* Potato Factory Tower Button */}
                <button class="towers" id="potatoFactory" type="Button" onClick={this.towers(4)}>
                    <p>Potato Factory</p>
                    <p class="towerText" id="factoryText">Cost: 1,000</p>
                    <p class="towerDesc" id="factoryDesc">Streamline Production in Potato Factories. &#40;{this.state.factoryPpSadd} PpS&#41;</p>
                </button>

                {/* Industrial District Tower Button */}
                <button class="towers" id="industrialDistrict" type="Button" onClick={this.towers(5)}>
                    <p>Industrial Disrict</p>
                    <p class="towerText" id="districtText">Cost: 5,000</p>
                    <p class="towerDesc" id="districtDesc">An entire district of factories dedicated to Potatoes.  &#40;{this.state.districtPpSadd} PpS&#41;</p>
                </button>

                <br />

                {/* Potato Convention Tower Button */}
                <button class="towers" id="potatoConvention" type="Button" onClick={this.towers(6)}>
                    <p>Potato Convention</p>
                    <p class="towerText" id="conventionText">Cost: 20,000</p>
                    <p class="towerDesc" id="conventionDesc">Host a convention for potatoes - "Potato-Con"! &#40;{this.state.conventionPpSadd} PpS&#41;</p>
                </button>

                {/* Potato Merchandise Tower Button */}
                <button class="towers" id="potatoMerchandise" type="Button" onClick={this.towers(7)}>
                    <p>Potato Merchandise</p>
                    <p class="towerText" id="merchandiseText">Cost: 100,000</p>
                    <p class="towerDesc" id="merchandiseDesc">Sell Merchandise with Potato Branding. &#40;{this.state.merchandisePpSadd} PpS&#41;</p>
                </button>

                <br />

                {/* Potato Cult Tower Button */}
                <button class="towers" id="potatoCult" type="Button" onClick={this.towers(8)}>
                    <p>Potato Cult</p>
                    <p class="towerText" id="cultText">Cost: 666,666</p>
                    <p class="towerDesc" id="cultDesc">Start Cults worshipping Potatoes. &#40;{this.state.cultPpSadd} PpS&#41;</p>
                </button>
        
                {/* Potato Sponsorships Tower Button */}
                <button class="towers" id="potatoSponsorships" type="Button" onClick={this.towers(9)}>
                    <p>Potato Sponsorships</p>
                    <p class="towerText" id="sponsorshipsText">Cost: 1,234,567</p>
                    <p class="towerDesc" id="sponsorshipsDesc"></p>
                </button>
            </div>
        )
    }
}

export default Towers;
