import React, {Component} from "react";
import "./main.css";

class Save extends Component {
    constructor(props) {
        super(props);

        this.state = {
            mainStats: props.mainStats,
            towers: props.mainStats
        }
    }

    saveData = () => {
        localStorage.setItem("mainStats", this.state.mainStats);
        localStorage.setItem("towers", this.state.towers);
    }

    retrieveData = () => {
        var mainStats = this.state.mainStats;
        var towers = this.state.towers;
        
        mainStats = localStorage.getItem("mainStats");
        towers = localStorage.getItem("towers");

        this.setState({mainStats: mainStats});
        this.setState({towers: towers});
        this.props.updateMainStats(this.state.mainStats);
        this.props.giveTowers(this.state.towers);
    }


    render() {
        return(
            <div>
                <div id="surroundSave">
                    <div class="innerDiv" id="saveBlock">
                    <p id="saveText"></p>
                    </div>
                </div>
            </div>
        )
    }
}

export default Save;
