import React, {Component} from "react";
import "./main.css";

class Towers extends Component {
    constructor(props) {
        super(props);

        this.state = {
            main: props.state
        }
    }



towers = (number) => {
    if (number == 1) {
        if (main.potatoes >= main.spudSpitterCost) {
            spudSpitter += 1;
            potatoes -= spudSpitterCost;
            spudSpitterCost = Math.floor(10 * (1.25 ** spudSpitter));
            potatoUp += 1;

            if (potatoes < spudSpitterCost) {
                document.getElementById("spudSpitter").style.backgroundColor = '#006600';
            }

            document.getElementById("varCount").innerHTML = "Potatoes: " + potatoes.toLocaleString();
            document.getElementById("spudSpitterCount").innerHTML = "Spud Spitters: " + spudSpitter.toLocaleString();
            document.getElementById("spudSpitterText").innerHTML = "Cost: " + spudSpitterCost.toLocaleString();
            console.log(spudSpitter);
            }
    } else if (number == 2) {
        if (potatoes >= farmerCost) {
            farmer += 1;
            potatoes -= farmerCost;
            farmerCost = Math.floor(50 * (1.15 ** farmer));
            PpS += farmerPpSadd;

            if (potatoes < farmerCost) {
                document.getElementById("potatoFarmer").style.backgroundColor = '#006600';
            }

            document.getElementById("varCount").innerHTML = "Potatoes: " + potatoes.toLocaleString();
            document.getElementById("PpSCount").innerHTML = "PpS: " + PpS.toLocaleString();
            document.getElementById("farmerCount").innerHTML = "Potato Farmers: " + farmer.toLocaleString();
            document.getElementById("farmerText").innerHTML = "Cost: " + farmerCost.toLocaleString();
            console.log(farmer);
        }
    } else if (number == 3) {
        if (potatoes >= farmCost) {
            increaseFarmCount();
        }
    } else if (number == 4) {
        if (potatoes >= factoryCost) {
            increaseFactoryCount();
        }
    } else if (number == 5) {
        if (potatoes >= districtCost) {
            increaseDistrictCount();
        }
    } else if (number == 6) {
        if (potatoes >= conventionCost) {
            increaseConventionCount();
        }
    } else if (number == 7) {
        if (potatoes >= merchandiseCost) {
            increaseMerchandiseCount();
        }
    }  else if (number == 8) {
        if (potatoes >= cultCost) {
            increaseCultCount();
        }
    }  else if (number == 9) {
        if (potatoes >= sponsorshipsCost) {
            increaseSponsorshipsCount();
        }
    }
}



    render() {
        return(
            <div>
                {/* Make a Potato Button */}
                 <button type="button" onclick="clickFirstButton()">Make a Potato!</button>

                <br />
                <br />

                {/* Spud Spitter Tower Button */}
                <button class="towers" id="spudSpitter" type="button" onclick="this.towers(1)">
                    <p>Spud Spitter</p>
                    <p class="towerText" id="spudSpitterText">Cost: 10</p>
                    <p class="towerDesc">Makes more potatoes per click.</p>
                </button>

                <br />

                {/* Potato Farmer Tower Button */}
                <button class="towers" id="potatoFarmer" type="Button" onclick="towers(2)">
                    <p>Potato Farmer</p>
                    <p class="towerText" id="farmerText">Cost: 50</p>
                    <p class="towerDesc" id="farmerDesc"></p>
                </button>

                {/* Potato Farm Tower Button */}
                <button class="towers" id="potatoFarm" type="Button" onclick="towers(3)">
                    <p>Potato Farm</p>
                    <p class="towerText" id="farmText">Cost: 300</p>
                    <p class="towerDesc" id="farmDesc"></p>
                </button>

                <br />

                {/* Potato Factory Tower Button */}
                <button class="towers" id="potatoFactory" type="Button" onclick="towers(4)">
                    <p>Potato Factory</p>
                    <p class="towerText" id="factoryText">Cost: 1,000</p>
                    <p class="towerDesc" id="factoryDesc"></p>
                </button>

                {/* Industrial District Tower Button */}
                <button class="towers" id="industrialDistrict" type="Button" onclick="towers(5)">
                    <p>Industrial Disrict</p>
                    <p class="towerText" id="districtText">Cost: 5,000</p>
                    <p class="towerDesc" id="districtDesc"></p>
                </button>

                <br />

                {/* Potato Convention Tower Button */}
                <button class="towers" id="potatoConvention" type="Button" onclick="towers(6)">
                    <p>Potato Convention</p>
                    <p class="towerText" id="conventionText">Cost: 20,000</p>
                    <p class="towerDesc" id="conventionDesc"></p>
                </button>

                {/* Potato Merchandise Tower Button */}
                <button class="towers" id="potatoMerchandise" type="Button" onclick="towers(7)">
                    <p>Potato Merchandise</p>
                    <p class="towerText" id="merchandiseText">Cost: 100,000</p>
                    <p class="towerDesc" id="merchandiseDesc"></p>
                </button>

                <br />

                {/* Potato Cult Tower Button */}
                <button class="towers" id="potatoCult" type="Button" onclick="towers(8)">
                    <p>Potato Cult</p>
                    <p class="towerText" id="cultText">Cost: 666,666</p>
                    <p class="towerDesc" id="cultDesc"></p>
                </button>
        
                {/* Potato Sponsorships Tower Button */}
                <button class="towers" id="potatoSponsorships" type="Button" onclick="towers(9)">
                    <p>Potato Sponsorships</p>
                    <p class="towerText" id="sponsorshipsText">Cost: 1,234,567</p>
                    <p class="towerDesc" id="sponsorshipsDesc"></p>
                </button>
            </div>
        )
    }
}

export default Towers;
