import React, {Component} from "react";
import "./main.css";

class Save extends Component {
    constructor(props) {
        super(props);

        this.state = {
            mainStats: props.mainStats,
            towers: props.towers,

            saveText: ""
        }
    }

    saveData = () => {
        var mainStats = this.props.mainStats;
        var towers = this.props.towers;

        // localStorage.setItem("mainStats", mainStats);
        localStorage.setItem("towers", towers);

        for (let item in mainStats) {
            localStorage.setItem(JSON.stringify(item), mainStats[item]);
        }
        for (let item in towers) {
            localStorage.setItem(JSON.stringify(item), towers[item]);
        }


        this.state.saveText = "Game Saved"

        setTimeout(() => {
            this.state.saveText = "";
        }, 3000);
    }

    retrieveData = () => {
        var mainStats = this.state.mainStats;
        var towers = this.state.towers;
        
        if (mainStats.potatoes > 0) {
            mainStats = localStorage.getItem("mainStats");
            towers = localStorage.getItem("towers");

            this.setState({mainStats: mainStats});
            this.setState({towers: towers});
            this.props.updateMainStats(this.state.mainStats);
            this.props.giveTowers(this.state.towers);
        }
    }

    componentDidMount() {
        this.retrieveData();
        setInterval(() => {this.saveData()}, 60000);
    }


    render() {
        return(
            <div>
                <div id="surroundSave">
                    <div class="innerDiv" id="saveBlock">
                        <button onClick={this.saveData}>save</button>
                        <p id="saveText">{this.state.saveText}</p>
                    </div>
                </div>
            </div>
        )
    }
}

export default Save;
